class Employee:
    def __init__(self, name, manager=None):
        self.name = name
        self.manager = manager
        self.direct_reports = []

    def add_direct_report(self, employee):
        self.direct_reports.append(employee)

    def get_hierarchy_level(self):
        if self.manager is None:
            return 0  # CEO is at level 0
        return self.manager.get_hierarchy_level() + 1

    def get_subordinate_count(self):
        count = len(self.direct_reports)
        for report in self.direct_reports:
            count += report.get_subordinate_count()
        return count

    def get_manager_count(self):
        if self.manager is None:
            return 0
        return 1 + self.manager.get_manager_count()


def print_employee_hierarchy(ceo):
    stack = [(ceo, 0)]
    while stack:
        employee, indent_level = stack.pop()
        print('  ' * indent_level + f"{employee.name} (Level: {employee.get_hierarchy_level()}, Subordinates: {employee.get_subordinate_count()}, Managers above: {employee.get_manager_count()})")
        for report in reversed(employee.direct_reports):
            stack.append((report, indent_level + 1))


