from collections import defaultdict

class EmployeeHierarchy:
    def __init__(self, employees):
        self.employees = employees
        self.manager_map = {emp: manager for emp, manager in employees.items()}
        self.subordinates_map = defaultdict(list)
        for emp, manager in self.manager_map.items():
            self.subordinates_map[manager].append(emp)
        self.ceo = self._find_ceo()
        self.hierarchy_levels = {}
        self.subordinate_counts = {}
        self.manager_chains = {}
        self._compute_hierarchy_levels()
        self._compute_subordinate_counts()
        self._compute_manager_chains()

    def _find_ceo(self):
        for emp in self.manager_map:
            if self.manager_map[emp] is None:
                return emp
        return None

    def _compute_hierarchy_levels(self):
        stack = [(self.ceo, 1)]
        while stack:
            emp, level = stack.pop()
            self.hierarchy_levels[emp] = level
            for sub in self.subordinates_map.get(emp, []):
                stack.append((sub, level + 1))

    def _compute_subordinate_counts(self):
        def count_subordinates(emp):
            count = 0
            for sub in self.subordinates_map.get(emp, []):
                count += 1 + count_subordinates(sub)
            self.subordinate_counts[emp] = count
            return count
        if self.ceo:
            count_subordinates(self.ceo)

    def _compute_manager_chains(self):
        for emp in self.manager_map:
            chain = []
            current = emp
            while current != self.ceo and current is not None:
                if current != self.ceo:  # Avoid adding None or CEO repeatedly
                    chain.append(current)
                current = self.manager_map.get(current, None)
            chain.reverse()  # To show from top to bottom
            if self.ceo:
                chain.insert(0, self.ceo)
            self.manager_chains[emp] = chain

    def get_hierarchy_levels(self):
        return self.hierarchy_levels

    def get_subordinate_counts(self):
        return self.subordinate_counts

    def get_manager_chains(self):
        return self.manager_chains

