from collections import defaultdict

def analyze_organization(employees):
    # Build the hierarchy (employee -> manager)
    hierarchy = {emp: manager for emp, manager in employees}

    # Build the inverse hierarchy (manager -> list of direct reports)
    inverse_hierarchy = defaultdict(list)
    for emp, manager in employees:
        inverse_hierarchy[manager].append(emp)

    # 1. Compute Hierarchy Level for each employee
    level_cache = {}

    def get_level(employee):
        if employee not in hierarchy:
            return 1  # CEO's level
        if employee in level_cache:
            return level_cache[employee]
        manager = hierarchy[employee]
        level_cache[employee] = 1 + get_level(manager)
        return level_cache[employee]

    levels = {emp: get_level(emp) for emp in hierarchy}
    # Add CEO's level if not already included
    ceo = next(manager for manager in inverse_hierarchy if manager not in hierarchy)
    levels[ceo] = 1

    # 2. Compute Subordinate Count (including indirect)
    subordinate_count = {}

    def count_subordinates(employee):
        count = 0
        for subordinate in inverse_hierarchy.get(employee, []):
            count += 1 + count_subordinates(subordinate)
        return count

    for employee in set(hierarchy.keys()).union({ceo}):
        subordinate_count[employee] = count_subordinates(employee)

    # 3. Compute Manager Chain for each employee
    manager_chains = {}

    def get_manager_chain(employee):
        if employee not in hierarchy:
            return []  # CEO has no manager chain
        manager = hierarchy[employee]
        return [manager] + get_manager_chain(manager)

    for employee in set(hierarchy.keys()).union({ceo}):
        manager_chains[employee] = get_manager_chain(employee)

    return {
        'hierarchy_level': levels,
        'subordinate_count': subordinate_count,
        'manager_chain': manager_chains
    }

