def analyze_hierarchy(employees):
    # Initialize level and reports count for each employee
    hierarchy = {emp: {'level': 0, 'reports': 0, 'chain': []} for emp in employees}

    # Calculate levels and reporting chain
    for emp in employees:
        current = emp
        chain = []
        while current in employees and employees[current] is not None:
            chain.append(current)
            current = employees[current]
        chain.append(current)  # Add CEO
        hierarchy[emp]['chain'] = chain
        hierarchy[emp]['level'] = len(chain)

    # Calculate total reports (direct + indirect)
    for emp in employees:
        stack = [emp]
        reports = set()
        while stack:
            current = stack.pop()
            for e, manager in employees.items():
                if manager == current and e != current:
                    reports.add(e)
                    stack.append(e)
        hierarchy[emp]['reports'] = len(reports)

    # Prepare the output
    output = {}
    for emp in employees:
        output[emp] = {
            'Level': hierarchy[emp]['level'],
            'Total Reports': hierarchy[emp]['reports'],
            'Chain of Command': hierarchy[emp]['chain']
        }

    return output

