def analyze_hierarchy(employees):
    hierarchy = {}
    ceo = None
    for emp in employees:
        if emp.level == 1:
            ceo = emp
            break
    if not ceo:
        return {}

    def build_hierarchy(employee):
        subordinates = []
        for emp in employees:
            if emp.manager == employee:
                subordinates.append(emp)
                build_hierarchy(emp)
        employee.subordinates = subordinates

    build_hierarchy(ceo)

    for emp in employees:
        hierarchy[emp.name] = {
            'level': emp.level,
            'subordinates_count': emp.count_subordinates(),
            'manager': emp.manager.name if emp.manager else None
        }
    return hierarchy

