def decode_message(code):
    parts = code.split(':')
    decoded = []
    for part in parts:
        symbols = part.split()
        word = []
        for symbol in symbols:
            if symbol == '.':
                word.append('A')
            elif symbol == ',':
                word.append('B')
            elif symbol == '*':
                word.append('C')
            else:
                word.append(symbol)
        decoded.append(''.join(word))
    return ' '.join(decoded)

