def decode_message(message):
    parts = message.split(':')
    decoded = []
    for part in parts:
        if part.strip() == '':
            continue
        symbols = part.split()
        char = ''
        for sym in symbols:
            if sym == '*':
                char += '1'
            elif sym == ',':
                char += '0'
            else:
                char += ' '
        decoded_char = chr(int(char.strip(), 2))
        decoded.append(decoded_char)
    return ''.join(decoded)

