def identify_sequences(dna_sequence):
    sample_id = []
    codons = [dna_sequence[i:i+3] for i in range(0, len(dna_sequence), 3)]

    for codon in codons:
        if codon in ['TAA', 'TAG', 'TGA']:
            sample_id.append(codon)

    if 'AT' in dna_sequence:
        sample_id.append('AT_motif')

    consecutive_g = 0
    for base in dna_sequence:
        if base == 'G':
            consecutive_g += 1
            if consecutive_g >= 3:
                sample_id.append(f'{consecutive_g}G_repeat')
        else:
            consecutive_g = 0

    return sample_id
