class Solution:
    def closestEqualElement(self, nums: List[int], queries: List[List[int]]) -> List[int]:
        from collections import defaultdict
        import bisect
        pos = defaultdict(list)
        for i, num in enumerate(nums):
            pos[num].append(i)
        res = []
        for i, k in queries:
            num = nums[i]
            idx_list = pos[num]
            idx = bisect.bisect_left(idx_list, k)
            if idx < len(idx_list) and idx_list[idx] == k:
                res.append(k)
            else:
                left = idx_list[idx - 1] if idx > 0 else float('-inf')
                right = idx_list[idx] if idx < len(idx_list) else float('inf')
                if abs(left - k) <= abs(right - k):
                    res.append(left)
                else:
                    res.append(right)
        return res

