def find_nearest(nums, queries):
    # Create a dictionary to map each value to the list of indices where it appears
    value_indices = {}
    for idx, num in enumerate(nums):
        if num not in value_indices:
            value_indices[num] = []
        value_indices[num].append(idx)

    result = []
    for i, k in queries:
        target = nums[i]
        indices = value_indices.get(target, [])

        # If there are no other indices (only the current one or none), return -1
        if len(indices) <= 1:
            result.append(-1)
            continue

        # Find the position where k would be inserted in the sorted list of indices
        left, right = 0, len(indices)
        while left < right:
            mid = (left + right) // 2
            if indices[mid] < k:
                left = mid + 1
            else:
                right = mid

        # Check the closest candidates around the insertion point
        candidates = []
        if left > 0:
            candidates.append(indices[left - 1])
        if left < len(indices) and indices[left] != i:  # Ensure it's not the same as i
            candidates.append(indices[left])

        # Filter out the current index i if it's in the list
        valid_candidates = [c for c in candidates if c != i]

        if not valid_candidates:
            result.append(-1)
        else:
            # Find the candidate with the smallest absolute difference to k
            min_diff = float('inf')
            best_candidate = -1
            for c in valid_candidates:
                diff = abs(c - k)
                if diff < min_diff or (diff == min_diff and c < best_candidate):
                    min_diff = diff
                    best_candidate = c
            result.append(best_candidate)

    return result

