class Solution:
    def longestSpecialPath(self, nums: List[int], edges: List[List[int]]) -> List[int]:
        from collections import defaultdict, Counter
        graph = defaultdict(list)
        for u, v in edges:
            graph[u].append(v)
            graph[v].append(u)
        def dfs(node, parent):
            count = Counter()
            max_length = 0
            min_nodes = float('inf')
            for neighbor in graph[node]:
                if neighbor == parent:
                    continue
                length, nodes, sub_count = dfs(neighbor, node)
                if any(sub_count[val] + count[val] > 2 for val in sub_count):
                    continue
                count += sub_count
                if length > max_length or (length == max_length and nodes < min_nodes):
                    max_length = length
                    min_nodes = nodes
            count[nums[node]] += 1
            return max_length + nums[node], min_nodes + 1, count
        max_length, min_nodes, _ = dfs(0, -1)
        return [max_length, min_nodes] if max_length > 0 else [0, 0]

