from collections import defaultdict

def longest_special_path(tree, nums):
    if not tree:
        return (0, 0)

    max_len = 1
    min_nodes = 1
    n = len(tree)

    # Build adjacency list
    adj = defaultdict(list)
    for i in range(n):
        if tree[i] != -1:
            adj[tree[i]].append(i)
            adj[i].append(tree[i])

    def dfs(node, parent, freq, current_len):
        nonlocal max_len, min_nodes
        num = nums[node]
        freq[num] = freq.get(num, 0) + 1

        if freq[num] <= 2:
            if current_len + 1 > max_len:
                max_len = current_len + 1
                min_nodes = current_len + 1
            elif current_len + 1 == max_len:
                if current_len + 1 < min_nodes:
                    min_nodes = current_len + 1

        for neighbor in adj[node]:
            if neighbor != parent:
                dfs(neighbor, node, freq.copy(), current_len + 1)

    for i in range(n):
        dfs(i, -1, {}, 0)

    return (max_len, min_nodes)

