from collections import defaultdict

def find_longest_special_path(n, edges, nums):
    if n == 0:
        return (0, 0)

    # Build the tree adjacency list
    tree = defaultdict(list)
    for u, v in edges:
        tree[u].append(v)
        tree[v].append(u)

    max_length = 1
    count = 1

    def dfs(node, parent, freq):
        nonlocal max_length, count
        current_num = nums[node]
        freq[current_num] += 1

        if freq[current_num] <= 2:
            current_length = sum(1 for v in freq.values() if v > 0)
            if current_length > max_length:
                max_length = current_length
                count = 1
            elif current_length == max_length:
                count += 1

            for neighbor in tree[node]:
                if neighbor != parent:
                    dfs(neighbor, node, freq.copy())

        freq[current_num] -= 1

    # Start DFS from each node to explore all possible paths
    for start_node in range(n):
        dfs(start_node, -1, defaultdict(int))

    # Since each path is counted twice (u->v and v->u), we adjust the count
    return (max_length, count // 2)

