from collections import defaultdict

def longest_unique_path(n, nums):
    if n == 0:
        return (0, 0)

    tree = defaultdict(list)
    for i in range(1, n):
        tree[i].append((i - 1, nums[i - 1]))
        tree[i - 1].append((i, nums[i]))

    max_length = 0
    min_nodes = float('inf')

    def dfs(node, parent, path_values):
        nonlocal max_length, min_nodes
        current_value = nums[node]
        if current_value in path_values:
            path_length = len(path_values)
            if path_length > max_length:
                max_length = path_length
                min_nodes = path_length
            elif path_length == max_length:
                if path_length < min_nodes:
                    min_nodes = path_length
            return
        new_path_values = path_values.copy()
        new_path_values.add(current_value)
        for neighbor, _ in tree[node]:
            if neighbor != parent:
                dfs(neighbor, node, new_path_values)
        path_length = len(new_path_values)
        if path_length > max_length:
            max_length = path_length
            min_nodes = path_length
        elif path_length == max_length:
            if path_length < min_nodes:
                min_nodes = path_length

    for start_node in range(n):
        dfs(start_node, -1, set())

    return (max_length, min_nodes)

