from collections import defaultdict

def longest_unique_path(tree, nums):
    if not tree:
        return (0, 0)

    max_length = 0
    min_nodes = float('inf')

    def dfs(node, parent, path_values):
        nonlocal max_length, min_nodes
        current_value = nums[node]
        path_values.append(current_value)

        # Check if current path is unique
        if len(set(path_values)) == len(path_values):
            path_length = len(path_values)
            if path_length > max_length:
                max_length = path_length
                min_nodes = path_length
            elif path_length == max_length:
                if path_length < min_nodes:
                    min_nodes = path_length
        else:
            return

        for neighbor in tree[node]:
            if neighbor != parent:
                dfs(neighbor, node, path_values.copy())

    for node in range(len(tree)):
        dfs(node, -1, [])

    return (max_length, min_nodes if max_length != 0 else 0)

