class TreeNode:
    def __init__(self, val=0, left=None, right=None):
        self.val = val
        self.left = left
        self.right = right

def longest_unique_path(root):
    if not root:
        return (0, 0)

    max_len = 0
    min_nodes = float('inf')

    def dfs(node, unique_counts, current_len, current_nodes):
        nonlocal max_len, min_nodes
        if node.val in unique_counts:
            unique_counts[node.val] += 1
        else:
            unique_counts[node.val] = 1

        current_len += 1
        current_nodes += 1

        if all(v <= 1 for v in unique_counts.values()):
            if current_len > max_len:
                max_len = current_len
                min_nodes = current_nodes
            elif current_len == max_len:
                if current_nodes < min_nodes:
                    min_nodes = current_nodes

        for child in [node.left, node.right]:
            if child:
                dfs(child, unique_counts.copy(), current_len, current_nodes)

    dfs(root, {}, 0, 0)
    return (max_len, min_nodes)

