def longest_palindrome_subseq_with_operations(s, k):
    n = len(s)
    memo = {}

    def dp(l, r, remaining_k):
        if (l, r, remaining_k) in memo:
            return memo[(l, r, remaining_k)]
        if l > r:
            return 0
        if l == r:
            return 1
        if s[l] == s[r]:
            res = 2 + dp(l + 1, r - 1, remaining_k)
        else:
            if remaining_k > 0:
                res = 2 + dp(l + 1, r - 1, remaining_k - 1)
            else:
                res = max(dp(l + 1, r, remaining_k), dp(l, r - 1, remaining_k))
        memo[(l, r, remaining_k)] = res
        return res

    return dp(0, n - 1, k)

