    freq = defaultdict(int)
    n = len(nums)

    # Iterate through all possible subarrays of size k
    for i in range(n - k + 1):
        subarray = nums[i:i+k]
        # Count occurrences of each number in the subarray
        seen_in_subarray = set()
        for num in subarray:
            if num not in seen_in_subarray:
                freq[num] += 1
                seen_in_subarray.add(num)

    missing_numbers = []
    for num in freq:
        if freq[num] == 1:
            missing_numbers.append(num)

    if not missing_numbers:
        return -1
    else:
        return sum(missing_numbers)

