class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        diff = [0] * (n + 1)
        k = 0
        for l, r, val in queries:
            diff[l] += val
            if r + 1 < n:
                diff[r + 1] -= val
            k += 1
            current = 0
            valid = True
            for i in range(n):
                current += diff[i]
                if nums[i] > current:
                    valid = False
                    break
            if valid and all(nums[i] <= current for i in range(n)):
                return k
        return -1

