def find_related_index(matrix, target_index):
    """
    Finds the index of a related element in a 2D matrix.

    Args:
        matrix: A 2D list (list of lists) representing the matrix.
        target_index: The index (row number) of the element to find related elements for.

    Returns:
        The index (column number) of the related element, or -1 if no related element is found.
        If multiple related elements exist, returns the smallest index.
    """

    if not isinstance(matrix, list) or not all(isinstance(row, list) for row in matrix):
        return -1  # Invalid input: matrix must be a list of lists

    if not isinstance(target_index, int) or target_index < 0 or target_index >= len(matrix):
        return -1  # Invalid input: target_index must be a valid index within the matrix

    target_value = matrix[target_index][0]  # Value to search for in other rows
    related_index = -1

    for i in range(len(matrix)):
        if i != target_index:  # Don't compare the row with itself
            for k in range(len(matrix[i])):
                if matrix[i][k] == target_value:
                    if related_index == -1 or k < related_index:
                        related_index = k

    return related_index
