def longest_special_path(n, edges, values):
    """
    Finds the longest special path in the tree. A special path is defined as a path
    where no value appears more than twice.

    Args:
        n: The number of nodes in the tree.
        edges: A list of tuples representing the edges of the tree.
        values: A list of integers representing the value of each node.

    Returns:
        A tuple containing the length of the longest special path and the minimum
        number of nodes in such paths.
    """

    graph = [[] for _ in range(n)]
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    max_len = 0
    min_nodes = float('inf')

    def dfs(node, parent, path, path_values):
        nonlocal max_len, min_nodes

        new_path = path + [node]
        new_path_values = path_values + [values[node]]

        counts = {}
        for val in new_path_values:
            counts[val] = counts.get(val, 0) + 1

        is_special = True
        for val in counts:
            if counts[val] > 2:
                is_special = False
                break

        if is_special:
            path_len = len(new_path) - 1  # Length is number of edges
            if path_len > max_len:
                max_len = path_len
                min_nodes = len(new_path)
            elif path_len == max_len:
                min_nodes = min(min_nodes, len(new_path))

            for neighbor in graph[node]:
                if neighbor != parent:
                    dfs(neighbor, node, new_path, new_path_values)

    for start_node in range(n):
        dfs(start_node, -1, [], [])

    return max_len, min_nodes

