def longest_special_path(adj, nums):
    """
    Finds the length of the longest special path and the minimum number of nodes in such paths.

    Args:
        adj: A list of lists representing the adjacency list of the tree.
        nums: A list of integers representing the values of the nodes.

    Returns:
        A tuple containing the length of the longest special path and the minimum number of nodes in such paths.
    """

    n = len(nums)
    max_len = 0
    min_nodes = float('inf')

    def is_special(path):
        counts = {}
        for node in path:
            val = nums[node]
            counts[val] = counts.get(val, 0) + 1
            if counts[val] > 2:
                return False
        return True

    def dfs(node, path, visited):
        nonlocal max_len, min_nodes

        path.append(node)
        visited[node] = True

        if is_special(path):
            if len(path) > max_len:
                max_len = len(path)
                min_nodes = 1
            elif len(path) == max_len:
                min_nodes = min(min_nodes, 1)

        is_leaf = True
        for neighbor in adj[node]:
            if not visited[neighbor]:
                is_leaf = False
                dfs(neighbor, path, visited)

        if is_leaf and is_special(path):
            if len(path) > max_len:
                max_len = len(path)
                min_nodes = len(path)
            elif len(path) == max_len:
                min_nodes = min(min_nodes, len(path))

        path.pop()
        visited[node] = False

    for i in range(n):
        visited = [False] * n
        dfs(i, [], visited)

    if max_len == 0:
        return 0, 0
    else:
        return max_len, min_nodes

