def longest_increasing_path(matrix):
    if not matrix:
        return 0, 0

    rows, cols = len(matrix), len(matrix[0])
    dp = {}  # (row, col): (length, count)

    def dfs(row, col):
        if (row, col) in dp:
            return dp[(row, col)]

        length = 1
        count = 1
        val = matrix[row][col]

        directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
        for dr, dc in directions:
            new_row, new_col = row + dr, col + dc
            if 0 <= new_row < rows and 0 <= new_col < cols and matrix[new_row][new_col] > val:
                new_length, new_count = dfs(new_row, new_col)
                if new_length + 1 > length:
                    length = new_length + 1
                    count = new_count
                elif new_length + 1 == length:
                    count += new_count

        dp[(row, col)] = (length, count)
        return length, count

    max_length = 0
    total_count = 0

    for i in range(rows):
        for j in range(cols):
            length, count = dfs(i, j)
            if length > max_length:
                max_length = length
                total_count = count
            elif length == max_length:
                total_count += count

    return max_length, total_count

