def solve_organization(ceo, subordinates):
    """
    Solves the organizational hierarchy problem.

    Args:
        ceo: The name of the CEO.
        subordinates: A dictionary where keys are employee names and values are lists of their direct subordinates.

    Returns:
        A tuple containing:
            - level_map: A dictionary mapping employee names to their level in the organization.
            - subtree_size: A dictionary mapping employee names to the number of subordinates under them (including indirect).
            - manager_chain: A dictionary mapping employee names to a list of their managers up to the CEO.
    """

    level_map = {}
    subtree_size = {}
    manager_chain = {}

    def calculate_level(employee, level):
        level_map[employee] = level
        for sub in subordinates.get(employee, []):
            calculate_level(sub, level + 1)

    def calculate_subtree_size(employee):
        count = 1  # Count the employee themselves
        for sub in subordinates.get(employee, []):
            count += calculate_subtree_size(sub)
        subtree_size[employee] = count
        return count

    def calculate_manager_chain(employee, chain):
        manager_chain[employee] = chain[:]
        for sub in subordinates.get(employee, []):
            calculate_manager_chain(sub, chain + [employee])

    calculate_level(ceo, 1)
    calculate_subtree_size(ceo)
    calculate_manager_chain(ceo, [])

    return level_map, subtree_size, manager_chain
