                option2 = solve(i + 1, j, changes_left)
                option3 = solve(i, j - 1, changes_left)
                dp[(i, j, changes_left)] = max(option1, option2, option3)
                return dp[(i, j, changes_left)]
            else:
                dp[(i, j, changes_left)] = max(solve(i + 1, j, changes_left), solve(i, j - 1, changes_left))
                return dp[(i, j, changes_left)]

