class Solution:
    def levelOrder(self, root: Optional[TreeNode]) -> List[List[int]]:
        levels = []

        def order(node, level):
            if level >= len(levels):
                levels.append([])

            if node:
                levels[level].append(node.val)

                if node.left:
                    order(node.left, level + 1)

                if node.right:
                    order(node.right, level + 1)

        if not root:
            return []

        order(root, 0)
        return levels

