class Employee:
    def __init__(self, employee_id, name, manager_id=None):
        self.employee_id = employee_id
        self.name = name
        self.manager_id = manager_id
        self.direct_reports = []
        self.level = None
        self.subordinate_count = 0
        self.number_of_managers = 0

def build_hierarchy(employees):
    employee_map = {emp.employee_id: emp for emp in employees}

    for emp in employees:
        if emp.manager_id:
            employee_map[emp.manager_id].direct_reports.append(emp)

    return employee_map

def calculate_hierarchy_levels(employee_map, ceo_id):
    ceo = employee_map[ceo_id]
    ceo.level = 0
    queue = [ceo]

    while queue:
        current_employee = queue.pop(0)
        for report in current_employee.direct_reports:
            report.level = current_employee.level + 1
            queue.append(report)

def calculate_subordinate_count(employee_map):
    def count_subordinates(employee):
        count = 0
        for report in employee.direct_reports:
            count += 1 + count_subordinates(report)
        employee.subordinate_count = count
        return count

    for employee in employee_map.values():
        if employee.manager_id is None:  # Start from the top (CEO)
            count_subordinates(employee)

def calculate_number_of_managers(employee_map, ceo_id):
    ceo = employee_map[ceo_id]
    ceo.number_of_managers = 0
    queue = [ceo]

    while queue:
        current_employee = queue.pop(0)
        for report in current_employee.direct_reports:
            report.number_of_managers = current_employee.number_of_managers + 1
            queue.append(report)

def process_organizational_hierarchy(employees, ceo_id):
    employee_map = build_hierarchy(employees)
    calculate_hierarchy_levels(employee_map, ceo_id)
    calculate_subordinate_count(employee_map)
    calculate_number_of_managers(employee_map, ceo_id)

    return employee_map

