class OrganizationAnalyzer:
    def __init__(self, employees):
        self.employees = {emp.employee_id: emp for emp in employees}
        self.ceo = self._find_ceo()
        self._build_hierarchy()

    def _find_ceo(self):
        manager_ids = {emp.manager_id for emp in self.employees.values() if emp.manager_id is not None}
        for employee in self.employees.values():
            if employee.employee_id not in manager_ids:
                return employee
        return None  # Handle case where CEO cannot be determined

    def _build_hierarchy(self):
        for employee in self.employees.values():
            if employee.manager_id:
                manager = self.employees.get(employee.manager_id)
                if manager:
                    manager.add_subordinate(employee)

    def get_hierarchy_level(self, employee_id):
        employee = self.employees.get(employee_id)
        if not employee:
            return None

        level = 1
        current = employee
        while current.manager_id:
            current = self.employees.get(current.manager_id)
            if current:
                level += 1
            else:
                return None # or raise an exception
        return level

    def get_total_subordinates(self, employee_id):
        employee = self.employees.get(employee_id)
        if not employee:
            return 0

        count = 0
        queue = employee.subordinates[:]
        while queue:
            subordinate = queue.pop(0)
            count += 1
            queue.extend(subordinate.subordinates)
        return count

    def get_manager_chain(self, employee_id):
        employee = self.employees.get(employee_id)
        if not employee:
            return []

        chain = []
        current = employee
        while current.manager_id:
            current = self.employees.get(current.manager_id)
            if current:
                chain.append(current)
            else:
                return [] # or raise an exception
        return chain


