                return 0  # Handle formula errors gracefully
        elif isinstance(value, int):
            return value
        else:
            try:
                return int(value)
            except ValueError:
                print(f"Invalid value in cell {cell_address}: {value}")
                return 0 # Handle invalid values gracefully

    def evaluate_formula(self, formula: str) -> int:
        # Replace cell references with their values
        def replace_cell_references(match):
            cell_address = match.group(0)
            return str(self.get_cell(cell_address))

        formula = re.sub(r'[A-Z]+\d+', replace_cell_references, formula)

        try:
            return int(eval(formula))  # Evaluate the formula
        except (SyntaxError, NameError, TypeError, ZeroDivisionError) as e:
            raise ValueError(f"Invalid formula: {formula} - {e}")
