    counts = {}
    missing = set()

    for i in range(n - k + 1):
        subarray = tuple(nums[i:i+k])

        seen = set()
        for num in subarray:
            if num not in seen:
                if num not in counts:
                    counts[num] = 0
                counts[num] += 1
                seen.add(num)

    for num in counts:
        if counts[num] == 1:
            missing.add(num)

    if not missing:
        return -1
    else:
        return min(missing)

