    if not lucky_integers:
        return -1
    else:
        return max(lucky_integers)

def is_lucky(nums, k, num):
    """
    Check if an integer num is lucky in the array nums with subarray size k.
    An integer num is lucky if 10 appears in exactly one subarray of size k within nums.
    """
    count = 0
    for i in range(len(nums) - k + 1):
        subarray = nums[i:i+k]
        if 10 in subarray:
            count += 1
    return count == 1

