    def dp(i, j):
        if (i, j) in memo:
            return memo[(i, j)]

        if j == len(p):
            return i == len(s)

        if j + 1 < len(p) and p[j + 1] == '*':
            # Case 1: '*' matches zero occurrences of the preceding element
            case1 = dp(i, j + 2)

            # Case 2: '*' matches one or more occurrences of the preceding element
            case2 = False
            if i < len(s) and (p[j] == '.' or s[i] == p[j]):
                case2 = dp(i + 1, j)

            memo[(i, j)] = case1 or case2
