        else:
            # No '*' to consider, so we must match the current characters
            if i < len(s) and (p[j] == '.' or s[i] == p[j]):
                memo[(i, j)] = dp(i + 1, j + 1)
                return memo[(i, j)]
            else:
                memo[(i, j)] = False
                return False

    return dp(0, 0)

