    def longestSpecialPath(self, n, nums, edges):
        graph = [[] for _ in range(n)]
        for u, v in edges:
            graph[u].append(v)
            graph[v].append(u)

        max_length = 0
        min_nodes = float('inf')

        def dfs(node, parent):
            nonlocal max_length, min_nodes
            count = {}
            stack = [(node, parent, 1)]
            while stack:
                node, parent, depth = stack.pop()
                count[nums[node]] = count.get(nums[node], 0) + 1
                if count[nums[node]] > 2:
                    return
                for child in graph[node]:
                    if child!= parent:
                        stack.append((child, node, depth + 1))
            max_length = max(max_length, depth)
            min_nodes = min(min_nodes, depth) if max_length == depth else min_nodes

        for i in range(n):
            dfs(i, -1)
        return max_length, min_nodes

