            if count[nums[node]] > 2:
                return {}
            self.ans = max(self.ans, len(path) + 1)
            return count

        dfs(0, -1, [])
        return self.ans

    def minOperations(self, nums):
        operations = 0
        while any(num > 1 for num in nums):
            for i in range(len(nums)):
                if nums[i] > 1:
                    nums[i] //= 2
                    operations += 1
        return operations

