class Solution:
    def longestPath(self, n, nums):
        graph = defaultdict(list)
        for i in range(n):
            for j in range(i+1, n):
                if nums[i] == nums[j]:
                    graph[i].append(j)
                    graph[j].append(i)

        def dfs(node, parent):
            max_path = 0
            for child in graph[node]:
                if child!= parent:
                    max_path = max(max_path, dfs(child, node))
            return max_path + 1

        max_path = 0
        for i in range(n):
            max_path = max(max_path, dfs(i, -1))

        return max_path

