class Solution:
    def longestCommonPrefixAfterRemoval(self, words: List[str], k: int) -> List[int]:
        def common_prefix_length(strs):
            if not strs: return 0
            min_len = min(len(s) for s in strs)
            for i in range(min_len):
                if len(set(s[i] for s in strs)) > 1:
                    return i
            return min_len
        n = len(words)
        answer = [0] * n
        for i in range(n):
            remaining_words = words[:i] + words[i+1:]
            if len(remaining_words) < k:
                answer[i] = 0
            else:
                max_prefix = 0
                for comb in itertools.combinations(remaining_words, k):
                    max_prefix = max(max_prefix, common_prefix_length(comb))
                answer[i] = max_prefix
        return answer

