import itertools

def longest_common_prefix(words, k):
    if k > len(words):
        return 0

    max_prefix_length = 0
    for combination in itertools.combinations(range(len(words)), k):
        min_prefix_length = float('inf')
        for i in range(len(words[combination[0]])):
            prefix = words[combination[0]][:i+1]
            for j in combination[1:]:
                if not words[j].startswith(prefix):
                    break
            else:
                min_prefix_length = min(min_prefix_length, i+1)
        max_prefix_length = max(max_prefix_length, min_prefix_length)

    return max_prefix_length

