class Organization:
    def __init__(self):
        self.employees = {}

    def add_employee(self, id, name, manager_id):
        self.employees[id] = Employee(id, name, manager_id)

    def build_hierarchy(self):
        for employee in self.employees.values():
            if employee.manager_id in self.employees:
                self.employees[employee.manager_id].subordinates.append(employee)

    def get_hierarchy_level(self, employee_id):
        def get_level(employee_id, level):
            if employee_id not in self.employees or self.employees[employee_id].manager_id is None:
                return level
            return get_level(self.employees[employee_id].manager_id, level + 1)
        return get_level(employee_id, 1)

    def get_subordinate_count(self, employee_id):
        def count_subordinates(employee):
            count = 0
            for subordinate in employee.subordinates:
                count += 1 + count_subordinates(subordinate)
            return count
        return count_subordinates(self.employees[employee_id])

    def get_manager_chain(self, employee_id):
        def get_chain(employee_id, chain):
            if employee_id not in self.employees or self.employees[employee_id].manager_id is None:
                return chain
            chain.append(self.employees[employee_id].name)
            return get_chain(self.employees[employee_id].manager_id, chain)
        return get_chain(employee_id, [])[::-1]

    def analyze_hierarchy(self):
        for employee in self.employees.values():
            print(f"Employee ID: {employee.id}, Name: {employee.name}")
            print(f"Hierarchy Level: {self.get_hierarchy_level(employee.id)}")
            print(f"Subordinate Count: {self.get_subordinate_count(employee.id)}")
            print(f"Manager Chain: {self.get_manager_chain(employee.id)}")
