class HierarchyAnalyzer:
    def __init__(self, employees):
        self.employees = employees
        self.hierarchy = self.build_hierarchy()

    def build_hierarchy(self):
        hierarchy = {}
        for employee in self.employees:
            if employee.manager_id not in hierarchy:
                hierarchy[employee.manager_id] = []
            hierarchy[employee.manager_id].append(employee.id)
        return hierarchy

    def get_hierarchy_levels(self):
        levels = {}
        for employee in self.employees:
            level = self.get_level(employee.id)
            levels[employee.id] = level
        return levels

    def get_level(self, employee_id, level=0, visited=None):
        if visited is None:
            visited = set()
        if employee_id in visited:
            return level
        visited.add(employee_id)
        for employee in self.employees:
            if employee.id == employee_id:
                if employee.manager_id is None:
                    return level
                else:
                    return self.get_level(employee.manager_id, level + 1, visited)

    def get_employees_at_level(self, level):
        employees_at_level = []
        for employee in self.employees:
            if self.get_level(employee.id) == level:
                employees_at_level.append(employee.name)
        return employees_at_level

    def get_manager(self, employee_id):
        for employee in self.employees:
            if employee.id == employee_id:
                return employee.manager_id

    def get_direct_reports(self, manager_id):
        direct_reports = []
        for employee in self.employees:
            if employee.manager_id == manager_id:
                direct_reports.append(employee.name)
        return direct_reports

