class HierarchyAnalyzer:
    def __init__(self, ceo):
        self.ceo = ceo

    def get_organizational_hierarchy(self):
        hierarchy = {}
        self._build_hierarchy(self.ceo, hierarchy, 0)
        return hierarchy

    def _build_hierarchy(self, employee, hierarchy, level):
        hierarchy[employee.name] = {'title': employee.title, 'level': level, 'direct_reports': []}
        for report in employee.direct_reports:
            hierarchy[employee.name]['direct_reports'].append(report.name)
            self._build_hierarchy(report, hierarchy, level + 1)

    def get_employee_level(self, employee_name):
        hierarchy = self.get_organizational_hierarchy()
        for name, info in hierarchy.items():
            if name == employee_name:
                return info['level']
        return None

    def get_employee_manager(self, employee_name):
        hierarchy = self.get_organizational_hierarchy()
        for name, info in hierarchy.items():
            if name == employee_name:
                for manager_name, manager_info in hierarchy.items():
                    if employee_name in manager_info['direct_reports']:
                        return manager_name
        return None

    def get_direct_reports(self, manager_name):
        hierarchy = self.get_organizational_hierarchy()
        for name, info in hierarchy.items():
            if name == manager_name:
                return info['direct_reports']
        return None

