def find_pairs(nums1, nums2, k):
    result = []
    for i in range(len(nums1)):
        for j in range(len(nums2)):
            if nums1[i] + nums2[j] == k:
                result.append((nums1[i], nums2[j]))
    return result

def find_pairs_optimized(nums1, nums2, k):
    result = set()
    for num in nums1:
        if k - num in nums2:
            result.add((num, k - num))
    return list(result)

def find_pairs_sorted(nums1, nums2, k):
    nums2.sort()
    result = []
    for num in nums1:
        target = k - num
        if binary_search(nums2, target):
            result.append((num, target))
    return result

def binary_search(arr, target):
    left, right = 0, len(arr) - 1
    while left <= right:
        mid = (left + right) // 2
        if arr[mid] == target:
            return True
        elif arr[mid] < target:
            left = mid + 1
        else:
            right = mid - 1
    return False

