def find_pairs(nums, k):
    if k < 0:
        return []
    if k == 0:
        return [(num, num) for num in set(nums) if nums.count(num) > 1]
    nums_set = set(nums)
    pairs = set()
    for num in nums_set:
        if num + k in nums_set:
            pairs.add((num, num + k))
    return list(pairs)

