def find_disappeared_numbers(nums):
    result = []
    for i in range(len(nums)):
        index = abs(nums[i]) - 1
        if nums[index] > 0:
            nums[index] *= -1
    for i in range(len(nums)):
        if nums[i] > 0:
            result.append(i + 1)
    return result

