def find_max_and_min(array):
    if len(array) == 0:
        return None
    max_value = array[0]
    min_value = array[0]
    for value in array:
        if value > max_value:
            max_value = value
        elif value < min_value:
            min_value = value
    return max_value, min_value

def main():
    array = [10, 20, 30, 40, 50]
    result = find_max_and_min(array)
    if result is not None:
        print("Max value: ", result[0])
        print("Min value: ", result[1])

if __name__ == "__main__":
    main()

