def is_perfect_square(x: int) -> bool:
    if x < 2:
        return True
    left, right = 2, x // 2
    while left <= right:
        mid = left + (right - left) // 2
        num = mid * mid
        if num == x:
            return True
        elif num < x:
            left = mid + 1
        else:
            right = mid - 1
    return False

