class TreeNode:
    def __init__(self, x):
        self.val = x
        self.left = None
        self.right = None

def bottom_left_value(root):
    if not root:
        return None
    queue = [root]
    result = root.val
    while queue:
        result = queue[0].val
        queue = [child for node in queue for child in (node.left, node.right) if child]
    return result

def bottom_left_value_recursive(root):
    def dfs(node, depth):
        nonlocal result, max_depth
        if node:
            if depth > max_depth:
                max_depth = depth
                result = node.val
            dfs(node.left, depth + 1)
            dfs(node.right, depth + 1)

    result = None
    max_depth = 0
    dfs(root, 0)
    return result
