class Solution:
    def longestSpecialPath(self, n, nums, edges):
        graph = [[] for _ in range(n)]
        for u, v in edges:
            graph[u].append(v)
            graph[v].append(u)

        self.ans = 0
        self.min_nodes = float('inf')

        def dfs(node, parent):
            count = {}
            stack = [(node, parent, 1)]
            while stack:
                node, parent, depth = stack.pop()
                count[nums[node]] = count.get(nums[node], 0) + 1
                for child in graph[node]:
                    if child != parent:
                        stack.append((child, node, depth + 1))
                if count[nums[node]] > 2:
                    return
            self.ans = max(self.ans, depth)
            self.min_nodes = min(self.min_nodes, n) if self.ans == depth else self.min_nodes

        for i in range(n):
            dfs(i, -1)

        return self.ans, self.min_nodes

