def special_paths(n, nums):
    def is_special_path(path):
        for i in range(len(path) - 1):
            if abs(path[i] - path[i + 1]) > 2:
                return False
        return True

    def dfs(i, path):
        if i == n - 1:
            if is_special_path(path):
                return [path]
            else:
                return []
        paths = []
        for j in range(i + 1, n):
            paths.extend(dfs(j, path + [nums[j]]))
        return paths

    paths = dfs(0, [nums[0]])
    if not paths:
        return -1, -1
    max_length = max(len(path) for path in paths)
    min_hops = min(len(path) - 1 for path in paths if len(path) == max_length)
    return max_length, min_hops

