class Organization:
    def __init__(self):
        self.employees = {}
        self.ceo = None

    def add_employee(self, id, name, manager_id):
        employee = Employee(id, name, manager_id)
        self.employees[id] = employee
        if manager_id is None:
            self.ceo = employee
        else:
            manager = self.employees.get(manager_id)
            if manager:
                manager.subordinates.append(employee)

    def calculate_levels(self):
        def dfs(employee, level):
            employee.level = level
            for subordinate in employee.subordinates:
                dfs(subordinate, level + 1)

        dfs(self.ceo, 1)

    def calculate_subordinate_count(self):
        def dfs(employee):
            count = 0
            for subordinate in employee.subordinates:
                count += 1 + dfs(subordinate)
            return count

        for employee in self.employees.values():
            employee.subordinate_count = dfs(employee)

    def calculate_manager_chain(self):
        def dfs(employee, chain):
            employee.manager_chain = chain[:]
            for subordinate in employee.subordinates:
                dfs(subordinate, chain + [employee.name])

        dfs(self.ceo, [self.ceo.name])

    def print_results(self):
        for employee in self.employees.values():
            print(f"Employee: {employee.name}, Level: {employee.level}, Subordinate Count: {dfs(employee)}, Manager Chain: {employee.manager_chain}")

